import {
  EditOutlined,
  UserOutlined,
  MailOutlined,
  CalendarOutlined,
  SafetyOutlined,
  HistoryOutlined,
} from '@ant-design/icons';
import {
  ProCard,
  ProForm,
  ProFormText,
  ProFormDigit,
} from '@ant-design/pro-components';
import { FormattedMessage, Helmet, useIntl } from '@umijs/max';
import {
  Avatar,
  Button,
  Descriptions,
  Divider,
  List,
  message,
  Space,
  Tag,
  Typography,
} from 'antd';
import { createStyles } from 'antd-style';
import React, { useEffect, useState } from 'react';
import {
  getCurrentUserProfile,
  updateUserProfile,
  getUserActivityLogs,
} from '@/services/ant-design-pro/api';
import Settings from '../../../../config/defaultSettings';

const { Title, Text } = Typography;

const useStyles = createStyles(({ token: _token }) => {
  return {
    container: {
      padding: '24px',
      width: '100%',
    },
    profileCard: {
      marginBottom: '24px',
    },
    avatarSection: {
      textAlign: 'center',
      padding: '24px',
    },
    infoSection: {
      padding: '24px',
    },
    activityCard: {
      marginTop: '24px',
    },
    editButton: {
      marginBottom: '16px',
    },
  };
});

interface UserProfile {
  id: string;
  username: string;
  name?: string;
  email?: string;
  age?: number;
  role: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
  lastLoginAt?: string;
}

interface ActivityLog {
  id: string;
  action: string;
  description: string;
  ipAddress?: string;
  userAgent?: string;
  createdAt: string;
}

const UserCenter: React.FC = () => {
  const [userProfile, setUserProfile] = useState<UserProfile | null>(null);
  const [activityLogs, setActivityLogs] = useState<ActivityLog[]>([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [form] = ProForm.useForm();
  const { styles } = useStyles();
  const intl = useIntl();

  // 
  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      const response = await getCurrentUserProfile();
      if (response.success && response.data) {
        // API  AppUser  username, name, email 
        const apiUser = response.data as any;
        const profile: UserProfile = {
          id: apiUser.id || apiUser.userid || '',
          username: apiUser.username || '', //   username 
          name: apiUser.name || apiUser.username || '', // name  username
          email: apiUser.email,
          age: apiUser.age || 18,
          role: apiUser.access || 'user',
          isActive: apiUser.isActive || apiUser.isLogin || false,
          createdAt: apiUser.createdAt || '',
          updatedAt: apiUser.updatedAt || apiUser.updateAt || '',
          lastLoginAt: apiUser.lastLoginAt || '',
        };
        setUserProfile(profile);
        form.setFieldsValue({
          username: apiUser.username, //   username
          name: apiUser.name || apiUser.username, //  name username
          email: apiUser.email,
          age: apiUser.age || 18,
        });
      }
    } catch (error) {
      console.error(':', error);
      message.error(
        intl.formatMessage({
          id: 'pages.account.center.fetchFailed',
          defaultMessage: '',
        }),
      );
    } finally {
      setLoading(false);
    }
  };

  // 
  const fetchActivityLogs = async () => {
    try {
      const response = await getUserActivityLogs();
      if (response.success && response.data) {
        //  UserActivityLog[]  ActivityLog[] 
        const logs: ActivityLog[] = response.data.map((log) => ({
          id: log.id || '',
          action: log.action || '',
          description: log.description || '',
          ipAddress: log.ipAddress,
          userAgent: log.userAgent,
          createdAt: log.createdAt || '',
        }));
        setActivityLogs(logs);
      }
    } catch (error) {
      console.error(':', error);
      message.error(
        intl.formatMessage({
          id: 'pages.account.center.activityFailed',
          defaultMessage: '',
        }),
      );
    }
  };

  useEffect(() => {
    fetchUserProfile();
    fetchActivityLogs();
  }, []);

  // 
  useEffect(() => {
    if (editing && userProfile) {
      form.setFieldsValue({
        username: userProfile.username,
        name: userProfile.name,
        email: userProfile.email,
        age: userProfile.age,
      });
    }
  }, [editing, userProfile, form]);

  // 
  const handleUpdateProfile = async (values: any) => {
    try {
      //   username 
      const { username: _username, ...updateData } = values;

      const response = await updateUserProfile(updateData);
      if (response.success) {
        message.success(
          intl.formatMessage({
            id: 'pages.account.center.updateSuccess',
            defaultMessage: '',
          }),
        );
        setEditing(false);
        fetchUserProfile(); // 
      } else {
        message.error(
          response.errorMessage ||
            intl.formatMessage({
              id: 'pages.account.center.updateFailed',
              defaultMessage: '',
            }),
        );
      }
    } catch (error) {
      console.error(':', error);
      message.error(
        intl.formatMessage({
          id: 'pages.account.center.updateFailed',
          defaultMessage: '',
        }),
      );
    }
  };

  // 
  const handleCancelEdit = () => {
    setEditing(false);
    form.resetFields();
  };

  // 
  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleString('zh-CN');
  };

  // 
  const getRoleTagColor = (role: string) => {
    switch (role) {
      case 'admin':
        return 'red';
      case 'user':
        return 'blue';
      default:
        return 'default';
    }
  };

  // 
  const getActivityTag = (action: string) => {
    const actionMap: { [key: string]: { text: string; color: string } } = {
      login: {
        text: intl.formatMessage({
          id: 'pages.account.center.activity.login',
          defaultMessage: '',
        }),
        color: 'green',
      },
      logout: {
        text: intl.formatMessage({
          id: 'pages.account.center.activity.logout',
          defaultMessage: '',
        }),
        color: 'orange',
      },
      update_profile: {
        text: intl.formatMessage({
          id: 'pages.account.center.activity.updateProfile',
          defaultMessage: '',
        }),
        color: 'blue',
      },
      change_password: {
        text: intl.formatMessage({
          id: 'pages.account.center.activity.changePassword',
          defaultMessage: '',
        }),
        color: 'red',
      },
      view_profile: {
        text: intl.formatMessage({
          id: 'pages.account.center.activity.viewProfile',
          defaultMessage: '',
        }),
        color: 'purple',
      },
      // 
      create_user: { text: '', color: 'cyan' },
      update_user: { text: '', color: 'blue' },
      delete_user: { text: '', color: 'red' },
      activate_user: { text: '', color: 'green' },
      deactivate_user: { text: '', color: 'orange' },
      update_user_role: { text: '', color: 'purple' },
      bulk_action: { text: '', color: 'geekblue' },
    };
    return actionMap[action] || { text: action, color: 'default' };
  };

  if (loading) {
    return (
      <div>
        <FormattedMessage
          id="pages.account.center.loading"
          defaultMessage="..."
        />
      </div>
    );
  }

  if (!userProfile) {
    return (
      <div>
        <FormattedMessage
          id="pages.account.center.userNotExist"
          defaultMessage=""
        />
      </div>
    );
  }

  return (
    <div className={styles.container}>
      <Helmet>
        <title>
          {intl.formatMessage({
            id: 'menu.account.center',
            defaultMessage: '',
          })}
          {Settings.title && ` - ${Settings.title}`}
        </title>
      </Helmet>

      <Title level={2}>
        <UserOutlined style={{ marginRight: '8px' }} />
        <FormattedMessage
          id="pages.account.center.title"
          defaultMessage=""
        />
      </Title>

      {/*  */}
      <ProCard
        title={
          <FormattedMessage
            id="pages.account.center.profile"
            defaultMessage=""
          />
        }
        className={styles.profileCard}
        extra={
          <Button
            type="primary"
            icon={<EditOutlined />}
            onClick={() => setEditing(true)}
            disabled={editing}
          >
            <FormattedMessage
              id="pages.account.center.editProfile"
              defaultMessage=""
            />
          </Button>
        }
      >
        <div className={styles.avatarSection}>
          <Avatar size={80} icon={<UserOutlined />} />
          <div style={{ marginTop: '16px' }}>
            <Title level={4}>{userProfile.name || userProfile.username}</Title>
            <Tag color={getRoleTagColor(userProfile.role)}>
              {userProfile.role === 'admin' ? (
                <FormattedMessage
                  id="pages.account.center.admin"
                  defaultMessage=""
                />
              ) : (
                <FormattedMessage
                  id="pages.account.center.user"
                  defaultMessage=""
                />
              )}
            </Tag>
          </div>
        </div>

        <Divider />

        {editing ? (
          <ProForm
            form={form}
            onFinish={handleUpdateProfile}
            submitter={{
              searchConfig: {
                submitText: '',
                resetText: '',
              },
              render: (_props, _doms) => {
                return (
                  <div style={{ textAlign: 'right' }}>
                    <Space>
                      <Button onClick={handleCancelEdit}></Button>
                      <Button type="primary" htmlType="submit">

                      </Button>
                    </Space>
                  </div>
                );
              },
              resetButtonProps: {
                style: { display: 'none' },
              },
              submitButtonProps: {
                style: { display: 'none' },
              },
            }}
          >
            <ProFormText
              name="username"
              label={
                <FormattedMessage
                  id="pages.account.center.username"
                  defaultMessage=""
                />
              }
              disabled
              tooltip=""
              fieldProps={{
                style: { color: 'rgba(0, 0, 0, 0.45)' },
              }}
            />
            <ProFormText
              name="name"
              label={
                <FormattedMessage
                  id="pages.account.center.name"
                  defaultMessage=""
                />
              }
              placeholder=""
            />
            <ProFormText
              name="email"
              label={
                <FormattedMessage
                  id="pages.account.center.email"
                  defaultMessage=""
                />
              }
              rules={[{ type: 'email', message: '' }]}
            />
            <ProFormDigit
              name="age"
              label={
                <FormattedMessage
                  id="pages.account.center.age"
                  defaultMessage=""
                />
              }
              min={1}
              max={150}
              placeholder=""
            />
          </ProForm>
        ) : (
          <Descriptions column={2} bordered>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.username"
                  defaultMessage=""
                />
              }
            >
              <Text strong>{userProfile.username}</Text>
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.name"
                  defaultMessage=""
                />
              }
            >
              {userProfile.name || (
                <FormattedMessage
                  id="pages.account.center.notSet"
                  defaultMessage=""
                />
              )}
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.email"
                  defaultMessage=""
                />
              }
            >
              <MailOutlined style={{ marginRight: '4px' }} />
              {userProfile.email || (
                <FormattedMessage
                  id="pages.account.center.notSet"
                  defaultMessage=""
                />
              )}
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.age"
                  defaultMessage=""
                />
              }
            >
              {userProfile.age || (
                <FormattedMessage
                  id="pages.account.center.notSet"
                  defaultMessage=""
                />
              )}
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.role"
                  defaultMessage=""
                />
              }
            >
              <Tag color={getRoleTagColor(userProfile.role)}>
                {userProfile.role === 'admin' ? (
                  <FormattedMessage
                    id="pages.account.center.admin"
                    defaultMessage=""
                  />
                ) : (
                  <FormattedMessage
                    id="pages.account.center.user"
                    defaultMessage=""
                  />
                )}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.status"
                  defaultMessage=""
                />
              }
            >
              <Tag color={userProfile.isActive ? 'green' : 'red'}>
                {userProfile.isActive ? (
                  <FormattedMessage
                    id="pages.account.center.active"
                    defaultMessage=""
                  />
                ) : (
                  <FormattedMessage
                    id="pages.account.center.inactive"
                    defaultMessage=""
                  />
                )}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.registerTime"
                  defaultMessage=""
                />
              }
            >
              <CalendarOutlined style={{ marginRight: '4px' }} />
              {formatDateTime(userProfile.createdAt)}
            </Descriptions.Item>
            <Descriptions.Item
              label={
                <FormattedMessage
                  id="pages.account.center.lastUpdate"
                  defaultMessage=""
                />
              }
            >
              <CalendarOutlined style={{ marginRight: '4px' }} />
              {formatDateTime(userProfile.updatedAt)}
            </Descriptions.Item>
            {userProfile.lastLoginAt && (
              <Descriptions.Item
                label={
                  <FormattedMessage
                    id="pages.account.center.lastLogin"
                    defaultMessage=""
                  />
                }
                span={2}
              >
                <CalendarOutlined style={{ marginRight: '4px' }} />
                {formatDateTime(userProfile.lastLoginAt)}
              </Descriptions.Item>
            )}
          </Descriptions>
        )}
      </ProCard>

      {/*  */}
      <ProCard
        title={
          <Space>
            <HistoryOutlined />
            <FormattedMessage
              id="pages.account.center.recentActivity"
              defaultMessage=""
            />
          </Space>
        }
        className={styles.activityCard}
      >
        <List
          dataSource={activityLogs}
          renderItem={(item) => {
            const activityTag = getActivityTag(item.action);
            return (
              <List.Item>
                <List.Item.Meta
                  avatar={<SafetyOutlined />}
                  title={
                    <Space>
                      <Tag color={activityTag.color}>{activityTag.text}</Tag>
                      <Text>{item.description}</Text>
                    </Space>
                  }
                  description={
                    <Space direction="vertical" size="small">
                      <Text type="secondary">
                        {formatDateTime(item.createdAt)}
                      </Text>
                      {item.ipAddress && (
                        <Text type="secondary" style={{ fontSize: '12px' }}>
                          IP: {item.ipAddress}
                        </Text>
                      )}
                    </Space>
                  }
                />
              </List.Item>
            );
          }}
        />
      </ProCard>
    </div>
  );
};

export default UserCenter;

