import {
  SearchOutlined,
  BankOutlined,
  TeamOutlined,
  CheckCircleOutlined,
} from '@ant-design/icons';
import { ProCard } from '@ant-design/pro-components';
import { PageContainer } from '@ant-design/pro-layout';
import {
  Input,
  Card,
  List,
  Button,
  Tag,
  Space,
  Empty,
  Spin,
  App,
} from 'antd';
import React, { useState } from 'react';
import { searchCompanies, applyToJoinCompany } from '@/services/company';

const { TextArea } = Input;

/**
 * v3.1: 
 * 
 */
const CompanySearch: React.FC = () => {
  const { message, modal } = App.useApp();
  const [keyword, setKeyword] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState<API.CompanySearchResult[]>(
    [],
  );
  const [applyingId, setApplyingId] = useState<string>('');

  // 
  const handleSearch = async () => {
    if (!keyword.trim()) {
      message.warning('');
      return;
    }

    setLoading(true);
    try {
      const response = await searchCompanies(keyword.trim());

      if (response.success && response.data) {
        setSearchResults(response.data);
        if (response.data.length === 0) {
          message.info('');
        }
      } else {
        message.error(response.errorMessage || '');
      }
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  // 
  const handleApply = (company: API.Company) => {
    let reason = '';

    modal.confirm({
      title: `${company.name}`,
      content: (
        <div style={{ marginTop: 16 }}>
          <div style={{ marginBottom: 8 }}></div>
          <TextArea
            rows={4}
            placeholder="..."
            onChange={(e) => {
              reason = e.target.value;
            }}
          />
        </div>
      ),
      okText: '',
      cancelText: '',
      onOk: async () => {
        setApplyingId(company.id || '');
        try {
          const response = await applyToJoinCompany({
            companyId: company.id || '',
            reason: reason || undefined,
          });

          if (response.success) {
            message.success('');
            // 
            await handleSearch();
          } else {
            message.error(response.errorMessage || '');
          }
        } catch (error: any) {
          message.error(error.message || '');
        } finally {
          setApplyingId('');
        }
      },
    });
  };

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProCard>
        {/*  */}
        <Space.Compact style={{ width: '100%', marginBottom: 24 }}>
          <Input
            size="large"
            placeholder="..."
            prefix={<SearchOutlined />}
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
            onPressEnter={handleSearch}
          />
          <Button
            type="primary"
            size="large"
            onClick={handleSearch}
            loading={loading}
          >

          </Button>
        </Space.Compact>

        {/*  */}
        {loading ? (
          <div style={{ textAlign: 'center', padding: 60 }}>
            <Spin size="large" tip="..." />
          </div>
        ) : searchResults.length > 0 ? (
          <List
            grid={{
              gutter: 16,
              xs: 1,
              sm: 1,
              md: 2,
              lg: 2,
              xl: 3,
              xxl: 3,
            }}
            dataSource={searchResults}
            renderItem={(item) => (
              <List.Item>
                <Card
                  hoverable
                  actions={[
                    item.isMember ? (
                      <Button key="joined" type="text" disabled>
                        <CheckCircleOutlined /> 
                      </Button>
                    ) : item.hasPendingRequest ? (
                      <Button key="pending" type="text" disabled>
                        ...
                      </Button>
                    ) : (
                      <Button
                        key="apply"
                        type="primary"
                        onClick={() => handleApply(item.company)}
                        loading={applyingId === item.company.id}
                      >

                      </Button>
                    ),
                  ]}
                >
                  <Card.Meta
                    avatar={
                      <BankOutlined
                        style={{ fontSize: 32, color: '#1890ff' }}
                      />
                    }
                    title={
                      <Space>
                        {item.company.name}
                        {item.isMember && (
                          <Tag color="success">
                            {item.memberStatus === 'active'
                              ? ''
                              : ''}
                          </Tag>
                        )}
                        {item.hasPendingRequest && (
                          <Tag color="processing"></Tag>
                        )}
                      </Space>
                    }
                    description={
                      <div>
                        <div style={{ marginBottom: 8 }}>
                          <span style={{ color: '#666' }}></span>
                          <span style={{ fontWeight: 500 }}>
                            {item.company.code}
                          </span>
                        </div>
                        {item.company.description && (
                          <div style={{ marginBottom: 8, color: '#666' }}>
                            {item.company.description}
                          </div>
                        )}
                        <div style={{ marginTop: 12 }}>
                          <Space>
                            <TeamOutlined />
                            <span>{item.memberCount} </span>
                          </Space>
                        </div>
                      </div>
                    }
                  />
                </Card>
              </List.Item>
            )}
          />
        ) : keyword ? (
          <Empty description="" style={{ padding: 60 }} />
        ) : (
          <Empty
            description=""
            style={{ padding: 60 }}
          />
        )}
      </ProCard>
    </PageContainer>
  );
};

export default CompanySearch;

