import { ProTable } from '@ant-design/pro-components';
import { PageContainer } from '@ant-design/pro-layout';
import { Tag, Button, Space, App, Popconfirm } from 'antd';
import React, { useRef, useState } from 'react';
import { getMyRequests, cancelRequest } from '@/services/company';
import type { ActionType, ProColumns } from '@ant-design/pro-components';
import {
  ClockCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
} from '@ant-design/icons';

/**
 * v3.1: 
 */
const MyJoinRequests: React.FC = () => {
  const { message } = App.useApp();
  const actionRef = useRef<ActionType>();
  const [loading, setLoading] = useState(false);

  // 
  const handleCancel = async (id: string) => {
    setLoading(true);
    try {
      const response = await cancelRequest(id);

      if (response.success) {
        message.success('');
        actionRef.current?.reload();
      } else {
        message.error(response.errorMessage || '');
      }
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  const columns: ProColumns<API.JoinRequestDetail>[] = [
    {
      title: '',
      dataIndex: 'companyName',
      width: 200,
      render: (_, record) => <strong>{record.companyName}</strong>,
    },
    {
      title: '',
      dataIndex: 'reason',
      ellipsis: true,
      search: false,
      render: (text) => text || <span style={{ color: '#999' }}></span>,
    },
    {
      title: '',
      dataIndex: 'createdAt',
      width: 180,
      valueType: 'dateTime',
      sorter: true,
      search: false,
    },
    {
      title: '',
      dataIndex: 'status',
      width: 120,
      valueEnum: {
        pending: {
          text: '',
          status: 'Processing',
        },
        approved: {
          text: '',
          status: 'Success',
        },
        rejected: {
          text: '',
          status: 'Error',
        },
        cancelled: {
          text: '',
          status: 'Default',
        },
      },
      render: (_, record) => {
        const statusConfig: Record<
          string,
          { icon: React.ReactNode; color: string }
        > = {
          pending: { icon: <ClockCircleOutlined />, color: 'processing' },
          approved: { icon: <CheckCircleOutlined />, color: 'success' },
          rejected: { icon: <CloseCircleOutlined />, color: 'error' },
          cancelled: { icon: null, color: 'default' },
        };
        const config = statusConfig[record.status] || statusConfig.pending;

        return (
          <Tag icon={config.icon} color={config.color}>
            {record.status === 'pending' && ''}
            {record.status === 'approved' && ''}
            {record.status === 'rejected' && ''}
            {record.status === 'cancelled' && ''}
          </Tag>
        );
      },
    },
    {
      title: '',
      dataIndex: 'rejectReason',
      ellipsis: true,
      search: false,
      render: (_, record) => {
        if (record.status === 'approved') {
          return (
            <span style={{ color: '#52c41a' }}>
               {' '}
              {record.reviewedByName && `${record.reviewedByName}`}
            </span>
          );
        }
        if (record.status === 'rejected') {
          return (
            <span style={{ color: '#ff4d4f' }}>
               {record.rejectReason || ''}
            </span>
          );
        }
        return <span style={{ color: '#999' }}>-</span>;
      },
    },
    {
      title: '',
      valueType: 'option',
      width: 120,
      render: (_, record) => {
        if (record.status === 'pending') {
          return (
            <Popconfirm
              title=""
              onConfirm={() => handleCancel(record.id)}
              okText=""
              cancelText=""
            >
              <Button type="link" danger loading={loading}>

              </Button>
            </Popconfirm>
          );
        }
        return null;
      },
    },
  ];

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProTable<API.JoinRequestDetail>
        columns={columns}
        actionRef={actionRef}
        request={async (params, sort) => {
          try {
            const response = await getMyRequests();

            if (response.success && response.data) {
              return {
                data: response.data,
                success: true,
                total: response.data.length,
              };
            }

            return {
              data: [],
              success: false,
            };
          } catch (error) {
            return {
              data: [],
              success: false,
            };
          }
        }}
        rowKey="id"
        search={false}
        pagination={{
          defaultPageSize: 10,
          showSizeChanger: true,
        }}
        dateFormatter="string"
        toolBarRender={() => [
          <Button
            key="search"
            type="primary"
            onClick={() => {
              window.location.href = '/company/search';
            }}
          >

          </Button>,
        ]}
      />
    </PageContainer>
  );
};

export default MyJoinRequests;

