import { ProTable } from '@ant-design/pro-components';
import { PageContainer } from '@ant-design/pro-layout';
import { Tag, Button, Space, App, Modal, Input } from 'antd';
import React, { useRef, useState } from 'react';
import {
  getPendingRequests,
  approveRequest,
  rejectRequest,
} from '@/services/company';
import type { ActionType, ProColumns } from '@ant-design/pro-components';
import {
  ClockCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
} from '@ant-design/icons';

const { TextArea } = Input;

/**
 * v3.1: 
 */
const PendingJoinRequests: React.FC = () => {
  const { message, modal } = App.useApp();
  const actionRef = useRef<ActionType>();
  const [loading, setLoading] = useState(false);

  // 
  const handleApprove = async (record: API.JoinRequestDetail) => {
    Modal.confirm({
      title: ` ${record.username} `,
      content: (
        <div>
          <p>{record.reason || ''}</p>
          <p></p>
        </div>
      ),
      okText: '',
      cancelText: '',
      onOk: async () => {
        setLoading(true);
        try {
          const response = await approveRequest(record.id);

          if (response.success) {
            message.success('');
            actionRef.current?.reload();
          } else {
            message.error(response.errorMessage || '');
          }
        } catch (error: any) {
          message.error(error.message || '');
        } finally {
          setLoading(false);
        }
      },
    });
  };

  // 
  const handleReject = (record: API.JoinRequestDetail) => {
    let rejectReason = '';

    modal.confirm({
      title: ` ${record.username} `,
      content: (
        <div style={{ marginTop: 16 }}>
          <div style={{ marginBottom: 8 }}></div>
          <TextArea
            rows={4}
            placeholder="..."
            onChange={(e) => {
              rejectReason = e.target.value;
            }}
          />
        </div>
      ),
      okText: '',
      okButtonProps: { danger: true },
      cancelText: '',
      onOk: async () => {
        if (!rejectReason.trim()) {
          message.warning('');
          return Promise.reject();
        }

        setLoading(true);
        try {
          const response = await rejectRequest(record.id, {
            rejectReason: rejectReason.trim(),
          });

          if (response.success) {
            message.success('');
            actionRef.current?.reload();
          } else {
            message.error(response.errorMessage || '');
          }
        } catch (error: any) {
          message.error(error.message || '');
        } finally {
          setLoading(false);
        }
      },
    });
  };

  const columns: ProColumns<API.JoinRequestDetail>[] = [
    {
      title: '',
      dataIndex: 'username',
      width: 150,
      render: (_, record) => (
        <div>
          <div>
            <strong>{record.username}</strong>
          </div>
          {record.userEmail && (
            <div style={{ fontSize: 12, color: '#999' }}>
              {record.userEmail}
            </div>
          )}
        </div>
      ),
    },
    {
      title: '',
      dataIndex: 'reason',
      ellipsis: true,
      search: false,
      render: (text) => text || <span style={{ color: '#999' }}></span>,
    },
    {
      title: '',
      dataIndex: 'createdAt',
      width: 180,
      valueType: 'dateTime',
      sorter: true,
      search: false,
    },
    {
      title: '',
      dataIndex: 'status',
      width: 100,
      hideInTable: true,
      valueEnum: {
        pending: {
          text: '',
          status: 'Processing',
        },
      },
    },
    {
      title: '',
      valueType: 'option',
      width: 180,
      render: (_, record) => (
        <Space>
          <Button
            type="primary"
            size="small"
            onClick={() => handleApprove(record)}
            loading={loading}
            icon={<CheckCircleOutlined />}
          >

          </Button>
          <Button
            danger
            size="small"
            onClick={() => handleReject(record)}
            loading={loading}
            icon={<CloseCircleOutlined />}
          >

          </Button>
        </Space>
      ),
    },
  ];

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProTable<API.JoinRequestDetail>
        columns={columns}
        actionRef={actionRef}
        request={async (params, sort) => {
          try {
            const response = await getPendingRequests();

            if (response.success && response.data) {
              return {
                data: response.data,
                success: true,
                total: response.data.length,
              };
            }

            return {
              data: [],
              success: false,
            };
          } catch (error) {
            return {
              data: [],
              success: false,
            };
          }
        }}
        rowKey="id"
        search={false}
        pagination={{
          defaultPageSize: 10,
          showSizeChanger: true,
        }}
        dateFormatter="string"
      />
    </PageContainer>
  );
};

export default PendingJoinRequests;

