      {description}
    </div>
  </Card>
);

// 
const SystemStatus: React.FC<{
  status: 'healthy' | 'warning' | 'error';
  message: string;
  lastUpdate: string;
}> = ({ status, message, lastUpdate }) => {
  const statusConfig = {
    healthy: { color: '#52c41a', icon: <CheckCircleOutlined />, text: '' },
    warning: { color: '#faad14', icon: <ExclamationCircleOutlined />, text: '' },
    error: { color: '#ff4d4f', icon: <ExclamationCircleOutlined />, text: '' }
  };

  const config = statusConfig[status];

  let alertType: 'success' | 'warning' | 'error';
  if (status === 'healthy') {
    alertType = 'success';
  } else if (status === 'warning') {
    alertType = 'warning';
  } else {
    alertType = 'error';
  }

  return (
    <Alert
      message={
        <Space>
          {config.icon}
          <span>{config.text}</span>
          <Text type="secondary">- {message}</Text>
        </Space>
      }
      description={`: ${lastUpdate}`}
      type={alertType}
      showIcon
      style={{ borderRadius: '8px' }}
    />
  );
};

