import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  SettingOutlined,
  MoreOutlined,
} from '@ant-design/icons';
import {
  PageContainer,
  ProTable,
  ActionType,
  ProColumns,
} from '@ant-design/pro-components';
import {
  Button,
  message,
  Popconfirm,
  Space,
  Tag,
  Dropdown,
  Modal,
  Input,
  Badge,
} from 'antd';
import type { MenuProps } from 'antd';
import React, { useRef, useState } from 'react';
import {
  getAllRoles,
  getAllRolesWithStats,
  deleteRole,
} from '@/services/role/api';
import type { Role } from '@/services/role/types';
import RoleForm from './components/RoleForm';
import MenuPermissionModal from './components/MenuPermissionModal';

const RoleManagement: React.FC = () => {
  const actionRef = useRef<ActionType>();
  const [modalVisible, setModalVisible] = useState(false);
  const [menuPermissionModalVisible, setMenuPermissionModalVisible] =
    useState(false);
  const [currentRole, setCurrentRole] = useState<Role | undefined>();

  /**
   * 
   */
  const loadRoleData = async () => {
    try {
      const response = await getAllRolesWithStats();
      if (response.success && response.data) {
        return {
          data: response.data.roles,
          total: response.data.total,
          success: true,
        };
      }
      return {
        data: [],
        total: 0,
        success: false,
      };
    } catch (error) {
      message.error('');
      return {
        data: [],
        total: 0,
        success: false,
      };
    }
  };

  /**
   * 
   */
  const handleDelete = async (id: string, roleName: string) => {
    let deleteReason = '';
    Modal.confirm({
      title: `"${roleName}"`,
      content: (
        <div>
          <p></p>
          <p></p>
          <Input.TextArea
            rows={3}
            placeholder=""
            onChange={(e) => {
              deleteReason = e.target.value;
            }}
            maxLength={200}
          />
        </div>
      ),
      okText: '',
      cancelText: '',
      okType: 'danger',
      onOk: async () => {
        try {
          const response = await deleteRole(id, deleteReason);
          if (response.success) {
            message.success('');
            actionRef.current?.reload();
          } else {
            message.error(response.errorMessage || '');
          }
        } catch (error: any) {
          message.error(error.message || '');
        }
      },
    });
  };

  /**
   * 
   */
  const columns: ProColumns<Role>[] = [
    {
      title: '',
      dataIndex: 'name',
      key: 'name',
      render: (text, record: any) => (
        <Space>
          {text}
          {record.userCount > 0 && (
            <Badge
              count={record.userCount}
              style={{ backgroundColor: '#52c41a' }}
              title={`${record.userCount} `}
            />
          )}
        </Space>
      ),
    },
    {
      title: '',
      dataIndex: 'description',
      key: 'description',
      ellipsis: true,
    },
    {
      title: '',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (_, record) => (
        <Tag color={record.isActive ? 'success' : 'default'}>
          {record.isActive ? '' : ''}
        </Tag>
      ),
    },
    {
      title: '',
      key: 'stats',
      render: (_, record: any) => (
        <Space split="|">
          <span>: {record.userCount || 0}</span>
          <span>: {record.menuCount || 0}</span>
        </Space>
      ),
    },
    {
      title: '',
      dataIndex: 'createdAt',
      key: 'createdAt',
      valueType: 'dateTime',
    },
    {
      title: '',
      key: 'action',
      fixed: 'right',
      render: (_, record) => {
        const moreItems: MenuProps['items'] = [
          {
            key: 'menu-permission',
            icon: <SettingOutlined />,
            label: '',
            onClick: () => {
              setCurrentRole(record);
              setMenuPermissionModalVisible(true);
            },
          },
          {
            type: 'divider',
          },
          {
            key: 'delete',
            icon: <DeleteOutlined />,
            label: '',
            danger: true,
            onClick: () => {
              handleDelete(record.id!, record.name);
            },
          },
        ];

        return (
          <Space size="small">
            <Button
              type="link"
              size="small"
              icon={<EditOutlined />}
              onClick={() => {
                setCurrentRole(record);
                setModalVisible(true);
              }}
            >

            </Button>
            <Dropdown menu={{ items: moreItems }} trigger={['click']}>
              <Button type="link" size="small" icon={<MoreOutlined />}>

              </Button>
            </Dropdown>
          </Space>
        );
      },
    },
  ];

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProTable<Role>
        actionRef={actionRef}
        rowKey="id"
        search={false}
        toolBarRender={() => [
          <Button
            key="create"
            type="primary"
            icon={<PlusOutlined />}
            onClick={() => {
              setCurrentRole(undefined);
              setModalVisible(true);
            }}
          >

          </Button>,
        ]}
        request={loadRoleData}
        columns={columns}
      />

      <RoleForm
        visible={modalVisible}
        current={currentRole}
        onCancel={() => {
          setModalVisible(false);
          setCurrentRole(undefined);
        }}
        onSuccess={() => {
          setModalVisible(false);
          setCurrentRole(undefined);
          actionRef.current?.reload();
        }}
      />

      <MenuPermissionModal
        visible={menuPermissionModalVisible}
        role={currentRole}
        onCancel={() => {
          setMenuPermissionModalVisible(false);
          setCurrentRole(undefined);
        }}
        onSuccess={() => {
          setMenuPermissionModalVisible(false);
          setCurrentRole(undefined);
          message.success('');
        }}
      />
    </PageContainer>
  );
};

export default RoleManagement;

