import {
  PageContainer,
  ProTable,
  ActionType,
  ProColumns,
} from '@ant-design/pro-components';
import { Tag, Button } from 'antd';
import { EyeOutlined } from '@ant-design/icons';
import React, { useRef, useState } from 'react';
import { getUserActivityLogs } from '@/services/user-log/api';
import type { UserActivityLog } from '@/services/user-log/types';
import LogDetailDrawer from './components/LogDetailDrawer';

const UserLog: React.FC = () => {
  const actionRef = useRef<ActionType>(null);
  const [detailDrawerOpen, setDetailDrawerOpen] = useState(false);
  const [selectedLog, setSelectedLog] = useState<UserActivityLog | null>(null);

  const handleViewDetail = (record: UserActivityLog) => {
    setSelectedLog(record);
    setDetailDrawerOpen(true);
  };

  const handleCloseDetail = () => {
    setDetailDrawerOpen(false);
    setSelectedLog(null);
  };

  /**
   * 
   */
  const getActionTagColor = (action: string): string => {
    const colorMap: Record<string, string> = {
      // 
      login: 'green',
      logout: 'default',
      refresh_token: 'geekblue',
      register: 'blue',

      // 
      view_profile: 'lime',
      update_profile: 'purple',
      change_password: 'orange',
      view_activity_logs: 'cyan',
      activate_user: 'green',
      deactivate_user: 'volcano',
      bulk_action: 'magenta',
      update_user_role: 'gold',
      create_user: 'blue',
      view_users: 'cyan',
      view_statistics: 'purple',
      view_user: 'cyan',
      update_user: 'blue',
      delete_user: 'red',

      // 
      view_roles: 'cyan',
      create_role: 'blue',
      update_role: 'orange',
      delete_role: 'red',

      // 
      view_menus: 'cyan',
      create_menu: 'blue',
      update_menu: 'orange',
      delete_menu: 'red',

      // 
      view_notices: 'cyan',
      create_notice: 'blue',
      update_notice: 'orange',
      delete_notice: 'red',

      // 
      view_tags: 'cyan',
      create_tag: 'blue',
      update_tag: 'orange',
      delete_tag: 'red',

      // 
      view_rules: 'cyan',
      create_rule: 'blue',
      update_rule: 'orange',
      delete_rule: 'red',

      // 
      view_permissions: 'cyan',
      create_permission: 'blue',
      update_permission: 'orange',
      delete_permission: 'red',

      // 
      view_current_user: 'lime',
    };
    return colorMap[action] || 'default';
  };

  /**
   * 
   */
  const getActionText = (action: string): string => {
    const textMap: Record<string, string> = {
      // 
      login: '',
      logout: '',
      refresh_token: 'Token',
      register: '',

      // 
      view_profile: '',
      update_profile: '',
      change_password: '',
      view_activity_logs: '',
      activate_user: '',
      deactivate_user: '',
      bulk_action: '',
      update_user_role: '',
      create_user: '',
      view_users: '',
      view_statistics: '',
      view_user: '',
      update_user: '',
      delete_user: '',

      // 
      view_roles: '',
      create_role: '',
      update_role: '',
      delete_role: '',
      role_operation: '',

      // 
      view_menus: '',
      create_menu: '',
      update_menu: '',
      delete_menu: '',
      menu_operation: '',

      // 
      view_notices: '',
      create_notice: '',
      update_notice: '',
      delete_notice: '',
      notice_operation: '',

      // 
      view_tags: '',
      create_tag: '',
      update_tag: '',
      delete_tag: '',
      tag_operation: '',

      // 
      view_rules: '',
      create_rule: '',
      update_rule: '',
      delete_rule: '',
      rule_operation: '',

      // 
      view_permissions: '',
      create_permission: '',
      update_permission: '',
      delete_permission: '',
      permission_operation: '',

      // 
      view_current_user: '',
      user_operation: '',

      // HTTP
      get_request: 'GET',
      post_request: 'POST',
      put_request: 'PUT',
      delete_request: 'DELETE',
      patch_request: 'PATCH',
    };
    return textMap[action] || action;
  };

  const columns: ProColumns<UserActivityLog>[] = [
    {
      title: '',
      dataIndex: 'username',
      key: 'username',
      ellipsis: true,
    },
    {
      title: '',
      dataIndex: 'action',
      key: 'action',
      render: (_, record) => (
        <Tag color={getActionTagColor(record.action)}>
          {getActionText(record.action)}
        </Tag>
      ),
      valueType: 'select',
      valueEnum: {
        // 
        login: { text: '' },
        logout: { text: '' },
        refresh_token: { text: 'Token' },
        register: { text: '' },

        // 
        view_profile: { text: '' },
        update_profile: { text: '' },
        change_password: { text: '' },
        view_activity_logs: { text: '' },
        activate_user: { text: '' },
        deactivate_user: { text: '' },
        bulk_action: { text: '' },
        update_user_role: { text: '' },
        create_user: { text: '' },
        view_users: { text: '' },
        view_statistics: { text: '' },
        view_user: { text: '' },
        update_user: { text: '' },
        delete_user: { text: '' },

        // 
        view_roles: { text: '' },
        create_role: { text: '' },
        update_role: { text: '' },
        delete_role: { text: '' },

        // 
        view_menus: { text: '' },
        create_menu: { text: '' },
        update_menu: { text: '' },
        delete_menu: { text: '' },

        // 
        view_notices: { text: '' },
        create_notice: { text: '' },
        update_notice: { text: '' },
        delete_notice: { text: '' },

        // 
        view_tags: { text: '' },
        create_tag: { text: '' },
        update_tag: { text: '' },
        delete_tag: { text: '' },

        // 
        view_rules: { text: '' },
        create_rule: { text: '' },
        update_rule: { text: '' },
        delete_rule: { text: '' },

        // 
        view_permissions: { text: '' },
        create_permission: { text: '' },
        update_permission: { text: '' },
        delete_permission: { text: '' },

        // 
        view_current_user: { text: '' },
      },
    },
    {
      title: '',
      dataIndex: 'description',
      key: 'description',
      search: false,
    },
    {
      title: 'IP',
      dataIndex: 'ipAddress',
      key: 'ipAddress',
      search: false,
      ellipsis: true,
    },
    {
      title: '',
      dataIndex: 'userAgent',
      key: 'userAgent',
      search: false,
      ellipsis: true,
      hideInTable: true,
    },
    {
      title: '',
      dataIndex: 'createdAt',
      key: 'createdAt',
      valueType: 'dateTime',
      search: false,
      sorter: true,
    },
    {
      title: '',
      key: 'option',
      valueType: 'option',
      render: (_, record) => [
        <Button
          key="view"
          type="link"
          size="small"
          icon={<EyeOutlined />}
          onClick={() => handleViewDetail(record)}
        >

        </Button>,
      ],
    },
  ];

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProTable<UserActivityLog>
        actionRef={actionRef}
        rowKey="id"
        search={{
          labelWidth: 120,
          optionRender: (searchConfig, formProps, dom) => {
            const reversed = [...dom];
            reversed.reverse();
            return reversed;
          },
        }}
        request={async (params, sort) => {
          const { current = 1, pageSize = 20, action } = params;

          try {
            const response = await getUserActivityLogs({
              page: current,
              pageSize,
              action,
            });

            if (response.success && response.data) {
              // { data: { data: [...], total: xxx, ... } }
              const result = response.data as any;
              return {
                data: result.data || [],
                total: result.total || 0,
                success: true,
              };
            }

            return {
              data: [],
              total: 0,
              success: false,
            };
          } catch (error) {
            console.error('Failed to load user activity logs:', error);
            return {
              data: [],
              total: 0,
              success: false,
            };
          }
        }}
        columns={columns}
        pagination={{
          defaultPageSize: 20,
          showSizeChanger: true,
          showQuickJumper: true,
        }}
      />

      <LogDetailDrawer
        open={detailDrawerOpen}
        log={selectedLog}
        onClose={handleCloseDetail}
      />
    </PageContainer>
  );
};

export default UserLog;

