import { useEffect, useState } from 'react';
import {
  PageContainer,
  ProCard,
  ProDescriptions,
} from '@ant-design/pro-components';
import { Card, message, Spin, Row, Col, Statistic, Progress, Tag } from 'antd';
import {
  UserOutlined,
  TeamOutlined,
  MenuOutlined,
  SafetyOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import { getCurrentCompany, getCompanyStatistics } from '@/services/company';
import EditCompanyModal from './components/EditCompanyModal';

export default function CompanySettings() {
  const [company, setCompany] = useState<API.Company | null>(null);
  const [statistics, setStatistics] = useState<API.CompanyStatistics | null>(
    null,
  );
  const [loading, setLoading] = useState(true);
  const [editModalVisible, setEditModalVisible] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [companyRes, statsRes] = await Promise.all([
        getCurrentCompany(),
        getCompanyStatistics(),
      ]);

      if (companyRes.success && companyRes.data) {
        setCompany(companyRes.data);
      }

      if (statsRes.success && statsRes.data) {
        setStatistics(statsRes.data);
      }
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  const handleEditSuccess = () => {
    setEditModalVisible(false);
    fetchData();
    message.success('');
  };

  if (loading) {
    return (
      <PageContainer>
        <div style={{ textAlign: 'center', padding: 100 }}>
          <Spin size="large" tip="..." />
        </div>
      </PageContainer>
    );
  }

  if (!company) {
    return (
      <PageContainer>
        <Card>
          <div style={{ textAlign: 'center', padding: 50 }}>
            <p></p>
          </div>
        </Card>
      </PageContainer>
    );
  }

  const userUsagePercent = statistics
    ? Math.round((statistics.totalUsers / statistics.maxUsers) * 100)
    : 0;

  return (
    <PageContainer
      title=""
      extra={[
        <a key="edit" onClick={() => setEditModalVisible(true)}>

        </a>,
      ]}
    >
      {/*  */}
      {statistics && (
        <ProCard
          title=""
          bordered
          headerBordered
          style={{ marginBottom: 24 }}
        >
          <Row gutter={16}>
            <Col span={8}>
              <Card bordered={false}>
                <Statistic
                  title=""
                  value={statistics.totalUsers}
                  suffix={`/ ${statistics.maxUsers}`}
                  prefix={<UserOutlined />}
                  valueStyle={{ color: '#3f8600' }}
                />
                <Progress
                  percent={userUsagePercent}
                  status={userUsagePercent >= 90 ? 'exception' : 'active'}
                  style={{ marginTop: 8 }}
                />
              </Card>
            </Col>
            <Col span={8}>
              <Card bordered={false}>
                <Statistic
                  title=""
                  value={statistics.activeUsers}
                  prefix={<UserOutlined />}
                />
              </Card>
            </Col>
            <Col span={8}>
              <Card bordered={false}>
                <Statistic
                  title=""
                  value={statistics.totalRoles}
                  prefix={<TeamOutlined />}
                />
              </Card>
            </Col>
          </Row>
          <Row gutter={16} style={{ marginTop: 16 }}>
            <Col span={8}>
              <Card bordered={false}>
                <Statistic
                  title=""
                  value={statistics.totalMenus}
                  prefix={<MenuOutlined />}
                />
              </Card>
            </Col>
            <Col span={8}>
              <Card bordered={false}>
                <Statistic
                  title=""
                  value={statistics.totalPermissions}
                  prefix={<SafetyOutlined />}
                />
              </Card>
            </Col>
            <Col span={8}>
              <Card bordered={false}>
                <div style={{ marginBottom: 8 }}>
                  <span style={{ color: '#00000073' }}></span>
                </div>
                <div>
                  {statistics.isExpired ? (
                    <Tag color="red"></Tag>
                  ) : company.isActive ? (
                    <Tag color="green"></Tag>
                  ) : (
                    <Tag color="orange"></Tag>
                  )}
                  {statistics.expiresAt && (
                    <div
                      style={{ marginTop: 8, fontSize: 12, color: '#00000073' }}
                    >
                      <ClockCircleOutlined /> 
                      {new Date(statistics.expiresAt).toLocaleDateString()}
                    </div>
                  )}
                </div>
              </Card>
            </Col>
          </Row>
        </ProCard>
      )}

      {/*  */}
      <ProCard title="" bordered headerBordered>
        <ProDescriptions
          column={2}
          dataSource={company}
          columns={[
            {
              title: '',
              dataIndex: 'name',
              copyable: true,
            },
            {
              title: '',
              dataIndex: 'code',
              copyable: true,
              render: (_, record) => <Tag color="blue">{record.code}</Tag>,
            },
            {
              title: '',
              dataIndex: 'industry',
              render: (text) => text || '-',
            },
            {
              title: '',
              dataIndex: 'contactName',
              render: (text) => text || '-',
            },
            {
              title: '',
              dataIndex: 'contactEmail',
              copyable: true,
              render: (text) => text || '-',
            },
            {
              title: '',
              dataIndex: 'contactPhone',
              copyable: true,
              render: (text) => text || '-',
            },
            {
              title: '',
              dataIndex: 'description',
              span: 2,
              render: (text) => text || '-',
            },
            {
              title: '',
              dataIndex: 'createdAt',
              valueType: 'dateTime',
            },
            {
              title: '',
              dataIndex: 'updatedAt',
              valueType: 'dateTime',
            },
          ]}
        />
      </ProCard>

      {/*  */}
      <EditCompanyModal
        visible={editModalVisible}
        company={company}
        onCancel={() => setEditModalVisible(false)}
        onSuccess={handleEditSuccess}
      />
    </PageContainer>
  );
}

