import React, { useRef, useState } from 'react';
import type { ActionType, ProColumns } from '@ant-design/pro-components';
import {
  PageContainer,
  ProTable,
  ProCard,
} from '@ant-design/pro-components';
import {
  Button,
  Tag,
  Space,
  Popconfirm,
  message,
  Modal,
  Select,
  Switch,
  Drawer,
  Statistic,
  Row,
  Col,
  Badge,
  Form,
  Input,
  Card,
  Dropdown,
  DatePicker,
} from 'antd';
import type { MenuProps } from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  UserOutlined,
  TeamOutlined,
  CheckCircleOutlined,
  EyeOutlined,
  ReloadOutlined,
  MoreOutlined,
  KeyOutlined,
} from '@ant-design/icons';
import { request } from '@umijs/max';
import { getAllRoles } from '@/services/role/api';
import type { AppUser, UserListRequest, UserStatisticsResponse } from './types';
import UserForm from './components/UserForm';
import UserDetail from './components/UserDetail';
import UserPermissionModal from './components/UserPermissionModal';
import PermissionControl from '@/components/PermissionControl';

const UserManagement: React.FC = () => {
  const actionRef = useRef<ActionType>(null);
  const [searchForm] = Form.useForm();
  const [selectedRows, setSelectedRows] = useState<AppUser[]>([]);
  const [formVisible, setFormVisible] = useState(false);
  const [detailVisible, setDetailVisible] = useState(false);
  const [permissionModalVisible, setPermissionModalVisible] = useState(false);
  const [editingUser, setEditingUser] = useState<AppUser | null>(null);
  const [viewingUser, setViewingUser] = useState<AppUser | null>(null);
  const [configuringUser, setConfiguringUser] = useState<AppUser | null>(null);
  const [statistics, setStatistics] = useState<UserStatisticsResponse | null>(null);
  const [roleMap, setRoleMap] = useState<Record<string, string>>({});
  const [searchParams, setSearchParams] = useState<UserListRequest>({
    Page: 1,
    PageSize: 10,
    SortBy: 'CreatedAt',
    SortOrder: 'desc',
  });

  // 
  React.useEffect(() => {
    const fetchRoles = async () => {
      try {
        const response = await getAllRoles();
        if (response.success && response.data) {
          //  ID 
          const map: Record<string, string> = {};
          response.data.roles.forEach(role => {
            if (role.id) {
              map[role.id] = role.name;
            }
          });
          setRoleMap(map);
        }
      } catch (error) {
        console.error(':', error);
      }
    };
    fetchRoles();
  }, []);

  // 
  const fetchStatistics = async () => {
    try {
      const response = await request<{ success: boolean; data: UserStatisticsResponse }>('/api/user/statistics', {
        method: 'GET',
      });
      setStatistics(response.data);
    } catch (error) {
      console.error(':', error);
      message.error('');
    }
  };

  // 
  const fetchUsers = async (params: any) => {
    console.log('fetchUsers :', params);
    console.log(':', searchParams);

    const requestData: UserListRequest = {
      Page: params.current || searchParams.Page,
      PageSize: params.pageSize || searchParams.PageSize,
      Search: searchParams.Search,
      RoleIds: searchParams.RoleIds,
      IsActive: searchParams.IsActive,
      SortBy: params.sortBy || searchParams.SortBy,
      SortOrder: params.sortOrder || searchParams.SortOrder,
      StartDate: searchParams.StartDate,
      EndDate: searchParams.EndDate,
    };

    console.log(':', requestData);

    try {
      const response = await request<{ success: boolean; data: any }>('/api/user/list', {
        method: 'POST',
        data: requestData,
      });

      console.log('API:', response);

      return {
        data: response.data.users || [],
        success: true,
        total: response.data.total || 0,
      };
    } catch (error) {
      console.error(':', error);
      message.error('');
      return {
        data: [],
        success: false,
        total: 0,
      };
    }
  };

  // 
  const handleSearch = (values: any) => {
    console.log(':', values);
    const newSearchParams: UserListRequest = {
      Page: 1,
      PageSize: searchParams.PageSize,
      Search: values.search,
      RoleIds: values.roleIds ? (Array.isArray(values.roleIds) ? values.roleIds : [values.roleIds]) : undefined,
      IsActive: values.isActive,
      SortBy: searchParams.SortBy,
      SortOrder: searchParams.SortOrder,
      StartDate: values.dateRange?.[0]?.format('YYYY-MM-DD'),
      EndDate: values.dateRange?.[1]?.format('YYYY-MM-DD'),
    };
    setSearchParams(newSearchParams);
    actionRef.current?.reload();
  };

  // 
  const handleReset = () => {
    searchForm.resetFields();
    const resetParams: UserListRequest = {
      Page: 1,
      PageSize: searchParams.PageSize,
      SortBy: 'CreatedAt',
      SortOrder: 'desc',
    };
    setSearchParams(resetParams);
    actionRef.current?.reload();
  };

  // 
  const handleDelete = async (userId: string) => {
    let deleteReason = '';
    Modal.confirm({
      title: '',
      content: (
        <div>
          <p></p>
          <Input.TextArea
            rows={3}
            placeholder=""
            onChange={(e) => { deleteReason = e.target.value; }}
            maxLength={200}
          />
        </div>
      ),
      okText: '',
      cancelText: '',
      okType: 'danger',
      onOk: async () => {
        try {
          await request(`/api/user/${userId}`, {
            method: 'DELETE',
            params: { reason: deleteReason },
          });
          message.success('');
          actionRef.current?.reload();
          fetchStatistics();
        } catch (error) {
          console.error(':', error);
          message.error('');
        }
      },
    });
  };

  // 
  const handleBulkAction = async (action: string) => {
    if (selectedRows.length === 0) {
      message.warning('');
      return;
    }

    // 
    if (action === 'delete') {
      let deleteReason = '';
      Modal.confirm({
        title: ` ${selectedRows.length} `,
        content: (
          <div>
            <p></p>
            <Input.TextArea
              rows={3}
              placeholder=""
              onChange={(e) => { deleteReason = e.target.value; }}
              maxLength={200}
            />
          </div>
        ),
        okText: '',
        cancelText: '',
        okType: 'danger',
        onOk: async () => {
          try {
            await request('/api/user/bulk-action', {
              method: 'POST',
              data: {
                UserIds: selectedRows.map(user => user.id),
                Action: action,
                Reason: deleteReason,
              },
            });

            message.success(``);
            setSelectedRows([]);
            actionRef.current?.reload();
            fetchStatistics();
          } catch (error) {
            console.error(':', error);
            message.error('');
          }
        },
      });
      return;
    }

    try {
      await request('/api/user/bulk-action', {
        method: 'POST',
        data: {
          UserIds: selectedRows.map(user => user.id),
          Action: action,
        },
      });

      const actionText = {
        activate: '',
        deactivate: '',
      }[action] || '';

      message.success(`${actionText}`);
      setSelectedRows([]);
      actionRef.current?.reload();
      fetchStatistics();
    } catch (error) {
      console.error(':', error);
      message.error('');
    }
  };

  // 
  const handleToggleStatus = async (user: AppUser) => {
    try {
      const endpoint = user.isActive ? 'deactivate' : 'activate';
      await request(`/api/user/${user.id}/${endpoint}`, {
        method: 'PUT',
      });

      message.success(`${user.isActive ? '' : ''}`);
      actionRef.current?.reload();
      fetchStatistics();
    } catch (error) {
      console.error(':', error);
      message.error('');
    }
  };

  // 
  const columns: ProColumns<AppUser>[] = [
    {
      title: '',
      dataIndex: 'username',
      key: 'username',
      render: (text, record) => (
        <Space>
          <UserOutlined />
          {text}
        </Space>
      ),
    },
    {
      title: '',
      dataIndex: 'email',
      key: 'email',
      ellipsis: true,
    },
    {
      title: '',
      dataIndex: 'roleIds',
      key: 'roleIds',
      render: (_, record) => {
        if (!record.roleIds || record.roleIds.length === 0) {
          return <Tag color="default"></Tag>;
        }
        return (
          <Space wrap>
            {record.roleIds.map(roleId => (
              <Tag key={roleId} color="blue">
                {roleMap[roleId] || roleId}
              </Tag>
            ))}
          </Space>
        );
      },
    },
    {
      title: '',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (_, record) => (
        <Badge
          status={record.isActive ? 'success' : 'error'}
          text={record.isActive ? '' : ''}
        />
      ),
      renderFormItem: (_, { record }) => (
        <Switch
          checked={record?.isActive}
          onChange={() => record && handleToggleStatus(record)}
        />
      ),
    },
    {
      title: '',
      dataIndex: 'createdAt',
      key: 'createdAt',
      valueType: 'dateTime',
      sorter: true,
    },
    {
      title: '',
      dataIndex: 'lastLoginAt',
      key: 'lastLoginAt',
      valueType: 'dateTime',
      render: (text) => text || '-',
    },
    {
      title: '',
      key: 'action',
      fixed: 'right',
      render: (_, record) => {
        const items: MenuProps['items'] = [
          {
            key: 'view',
            icon: <EyeOutlined />,
            label: '',
            onClick: () => {
              setViewingUser(record);
              setDetailVisible(true);
            },
          },
          {
            key: 'edit',
            icon: <EditOutlined />,
            label: '',
            onClick: () => {
              setEditingUser(record);
              setFormVisible(true);
            },
          },
          {
            key: 'permission',
            icon: <KeyOutlined />,
            label: '',
            onClick: () => {
              setConfiguringUser(record);
              setPermissionModalVisible(true);
            },
          },
          {
            type: 'divider',
          },
          {
            key: 'delete',
            icon: <DeleteOutlined />,
            label: '',
            danger: true,
            onClick: () => {
              record.id && handleDelete(record.id);
            },
          },
        ];

        return (
          <Space size="small">
            <Button
              type="link"
              size="small"
              icon={<EyeOutlined />}
              onClick={() => {
                setViewingUser(record);
                setDetailVisible(true);
              }}
            >

            </Button>
            <PermissionControl permission="user:update">
              <Button
                type="link"
                size="small"
                icon={<EditOutlined />}
                onClick={() => {
                  setEditingUser(record);
                  setFormVisible(true);
                }}
              >

              </Button>
            </PermissionControl>
            <Dropdown menu={{ items: items.slice(2) }} trigger={['click']}>
              <Button type="link" size="small" icon={<MoreOutlined />}>

              </Button>
            </Dropdown>
          </Space>
        );
      },
    },
  ];

  React.useEffect(() => {
    fetchStatistics();
  }, []);

  return (
    <PageContainer
      title=""
      extra={[
        <Button
          key="refresh"
          icon={<ReloadOutlined />}
          onClick={() => {
            actionRef.current?.reload();
            fetchStatistics();
          }}
        >

        </Button>,
        <PermissionControl permission="user:create" key="add">
          <Button
            type="primary"
            icon={<PlusOutlined />}
            onClick={() => {
              setEditingUser(null);
              setFormVisible(true);
            }}
          >

          </Button>
        </PermissionControl>,
      ]}
    >
      {/*  */}
      {statistics && (
        <ProCard style={{ marginBottom: 16 }}>
          <Row gutter={16}>
            <Col span={6}>
              <Statistic
                title=""
                value={statistics.totalUsers}
                prefix={<TeamOutlined />}
              />
            </Col>
            <Col span={6}>
              <Statistic
                title=""
                value={statistics.activeUsers}
                prefix={<CheckCircleOutlined />}
                valueStyle={{ color: '#3f8600' }}
              />
            </Col>
            <Col span={6}>
              <Statistic
                title=""
                value={statistics.adminUsers}
                prefix={<UserOutlined />}
                valueStyle={{ color: '#cf1322' }}
              />
            </Col>
            <Col span={6}>
              <Statistic
                title=""
                value={statistics.newUsersThisMonth}
                prefix={<PlusOutlined />}
                valueStyle={{ color: '#1890ff' }}
              />
            </Col>
          </Row>
        </ProCard>
      )}

      {/*  */}
      <Card style={{ marginBottom: 16 }}>
        <Form
          form={searchForm}
          layout="inline"
          onFinish={handleSearch}
          style={{ marginBottom: 16 }}
        >
          <Form.Item name="search" label="">
            <Input placeholder="" style={{ width: 200 }} />
          </Form.Item>
          <Form.Item name="roleIds" label="">
            <Select 
              mode="multiple"
              placeholder="" 
              style={{ width: 200 }} 
              allowClear
              loading={Object.keys(roleMap).length === 0}
            >
              {Object.entries(roleMap).map(([id, name]) => (
                <Select.Option key={id} value={id}>
                  {name}
                </Select.Option>
              ))}
            </Select>
          </Form.Item>
          <Form.Item name="isActive" label="">
            <Select placeholder="" style={{ width: 120 }} allowClear>
              <Select.Option value={true}></Select.Option>
              <Select.Option value={false}></Select.Option>
            </Select>
          </Form.Item>
          <Form.Item name="dateRange" label="">
            <DatePicker.RangePicker style={{ width: 240 }} />
          </Form.Item>
          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">

              </Button>
              <Button onClick={handleReset}>

              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Card>

      {/*  */}
      <ProTable<AppUser>
        actionRef={actionRef}
        rowKey="id"
        search={false}
        toolBarRender={() => [
          <Button
            key="activate"
            onClick={() => handleBulkAction('activate')}
            disabled={selectedRows.length === 0}
          >

          </Button>,
          <Button
            key="deactivate"
            onClick={() => handleBulkAction('deactivate')}
            disabled={selectedRows.length === 0}
          >

          </Button>,
          <Button
            key="delete"
            danger
            onClick={() => handleBulkAction('delete')}
            disabled={selectedRows.length === 0}
          >

          </Button>,
        ]}
        request={fetchUsers}
        columns={columns}
        rowSelection={{
          onChange: (_, selectedRows) => {
            setSelectedRows(selectedRows);
          },
        }}
        pagination={{
          defaultPageSize: 10,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: (total, range) =>
            ` ${range[0]}-${range[1]} / ${total} `,
        }}
      />

      {/*  */}
      <Modal
        title={editingUser ? '' : ''}
        open={formVisible}
        onCancel={() => setFormVisible(false)}
        footer={null}
        width={600}
      >
        <UserForm
          user={editingUser}
          onSuccess={() => {
            setFormVisible(false);
            actionRef.current?.reload();
            fetchStatistics();
          }}
          onCancel={() => setFormVisible(false)}
        />
      </Modal>

      {/*  */}
      <Drawer
        title=""
        open={detailVisible}
        onClose={() => setDetailVisible(false)}
        width={600}
      >
        {viewingUser && (
          <UserDetail
            user={viewingUser}
            onClose={() => setDetailVisible(false)}
          />
        )}
      </Drawer>

      {/*  */}
      <UserPermissionModal
        visible={permissionModalVisible}
        user={configuringUser}
        onCancel={() => {
          setPermissionModalVisible(false);
          setConfiguringUser(null);
        }}
        onSuccess={() => {
          setPermissionModalVisible(false);
          setConfiguringUser(null);
          actionRef.current?.reload();
        }}
      />
    </PageContainer>
  );
};

export default UserManagement;

