import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
import * as Icons from '@ant-design/icons';
import { PageContainer, ProTable, ActionType, ProColumns } from '@ant-design/pro-components';
import { Button, message, Popconfirm, Space, Tag, Modal, Input } from 'antd';
import React, { useRef, useState } from 'react';
import { getMenuTree, deleteMenu } from '@/services/menu/api';
import type { MenuTreeNode } from '@/services/menu/types';
import MenuForm from './components/MenuForm';
import PermissionControl from '@/components/PermissionControl';

const MenuManagement: React.FC = () => {
  const actionRef = useRef<ActionType>();
  const [modalVisible, setModalVisible] = useState(false);
  const [currentMenu, setCurrentMenu] = useState<MenuTreeNode | undefined>();

  /**
   * 
   */
  const getIconComponent = (iconName?: string): React.ReactNode => {
    if (!iconName) return null;

    const formatIconName = (name: string) => {
      return name
        .split('-')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
        .join('');
    };

    const suffixes = ['Outlined', 'Filled', 'TwoTone', ''];

    for (const suffix of suffixes) {
      const iconComponentName = formatIconName(iconName) + suffix;
      const IconComponent = (Icons as any)[iconComponentName];

      if (IconComponent) {
        return React.createElement(IconComponent);
      }
    }

    return null;
  };

  /**
   * 
   */
  const loadMenuData = async () => {
    try {
      const response = await getMenuTree();
      if (response.success && response.data) {
        return {
          data: response.data,
          success: true,
        };
      }
      return {
        data: [],
        success: false,
      };
    } catch (error) {
      message.error('');
      return {
        data: [],
        success: false,
      };
    }
  };

  /**
   * 
   */
  const handleDelete = async (id: string, menuName: string) => {
    let deleteReason = '';
    Modal.confirm({
      title: `"${menuName}"`,
      content: (
        <div>
          <p style={{ color: '#ff4d4f' }}>

          </p>
          <p style={{ color: '#ff4d4f' }}>

          </p>
          <p></p>
          <Input.TextArea
            rows={3}
            placeholder=""
            onChange={(e) => { deleteReason = e.target.value; }}
            maxLength={200}
          />
        </div>
      ),
      okText: '',
      cancelText: '',
      okType: 'danger',
      width: 520,
      onOk: async () => {
        try {
          const response = await deleteMenu(id, deleteReason);
          if (response.success) {
            message.success('');
            actionRef.current?.reload();
          } else {
            message.error(response.errorMessage || '');
          }
        } catch (error: any) {
          message.error(error.message || '');
        }
      },
    });
  };

  /**
   * 
   */
  const columns: ProColumns<MenuTreeNode>[] = [
    {
      title: '',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: '',
      dataIndex: 'path',
      key: 'path',
    },
    {
      title: '',
      dataIndex: 'icon',
      key: 'icon',
      render: (_, record) => (
        <Space>
          {getIconComponent(record.icon)}
          <span>{record.icon || '-'}</span>
        </Space>
      ),
    },
    {
      title: '',
      dataIndex: 'component',
      key: 'component',
      ellipsis: true,
    },
    {
      title: '',
      dataIndex: 'sortOrder',
      key: 'sortOrder',
    },
    {
      title: '',
      dataIndex: 'isEnabled',
      key: 'isEnabled',
      render: (_, record) => (
        <Tag color={record.isEnabled ? 'success' : 'default'}>
          {record.isEnabled ? '' : ''}
        </Tag>
      ),
    },
    {
      title: '',
      dataIndex: 'isExternal',
      key: 'isExternal',
      render: (_, record) => (
        <Tag color={record.isExternal ? 'blue' : 'default'}>
          {record.isExternal ? '' : ''}
        </Tag>
      ),
    },
    {
      title: '',
      dataIndex: 'hideInMenu',
      key: 'hideInMenu',
      render: (_, record) => (
        <Tag color={record.hideInMenu ? 'warning' : 'default'}>
          {record.hideInMenu ? '' : ''}
        </Tag>
      ),
    },
    {
      title: '',
      key: 'action',
      fixed: 'right',
      render: (_, record) => (
        <Space size="small">
          <PermissionControl permission="menu:update">
            <Button
              type="link"
              size="small"
              icon={<EditOutlined />}
              onClick={() => {
                setCurrentMenu(record);
                setModalVisible(true);
              }}
            >

            </Button>
          </PermissionControl>
          <PermissionControl permission="menu:delete">
            <Button 
              type="link" 
              size="small" 
              danger 
              icon={<DeleteOutlined />}
              onClick={() => handleDelete(record.id!, record.name)}
            >

            </Button>
          </PermissionControl>
        </Space>
      ),
    },
  ];

  return (
    <PageContainer
      header={{
        title: '',
        subTitle: '',
      }}
    >
      <ProTable<MenuTreeNode>
        actionRef={actionRef}
        rowKey="id"
        search={false}
        toolBarRender={() => [
          <PermissionControl permission="menu:create" key="create">
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={() => {
                setCurrentMenu(undefined);
                setModalVisible(true);
              }}
            >

            </Button>
          </PermissionControl>,
        ]}
        request={loadMenuData}
        columns={columns}
        pagination={false}
        expandable={{
          defaultExpandAllRows: true,
        }}
      />

      <MenuForm
        visible={modalVisible}
        current={currentMenu}
        onCancel={() => {
          setModalVisible(false);
          setCurrentMenu(undefined);
        }}
        onSuccess={() => {
          setModalVisible(false);
          setCurrentMenu(undefined);
          actionRef.current?.reload();
        }}
      />
    </PageContainer>
  );
};

export default MenuManagement;

