        // 
        view_roles: { text: '' },
        create_role: { text: '' },
        update_role: { text: '' },
        delete_role: { text: '' },

        // 
        view_menus: { text: '' },
        create_menu: { text: '' },
        update_menu: { text: '' },
        delete_menu: { text: '' },

        // 
        view_notices: { text: '' },
        create_notice: { text: '' },
        update_notice: { text: '' },
        delete_notice: { text: '' },

        // 
        view_tags: { text: '' },
        create_tag: { text: '' },
        update_tag: { text: '' },
        delete_tag: { text: '' },

